//import Vector from '../images/Vector.svg';
//import './index.css';
import React from 'react';
import Header from '../components/Header'
import Main from '../components/Main'
import Footer from '../components/Footer'
import PopupWithForm from '../components/PopupWithForm'
import api from './utils.js/Api';

function App() {
    //попап аватар
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false)

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true)
    };
    //попап добавления карточки
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false)

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true)
    };
    //попап профиль
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false)

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true)
    }
    //закрытие попапов 
    function closeAllPopups() {
        setIsEditAvatarPopupOpen(false)
        setIsAddPlacePopupOpen(false)
        setIsEditProfilePopupOpen(false)
    }
    //данные пользователя 
    const [userName, setuserName] = React.useState()

    function handleUserName() {
        api.search(userName)
    }
    return ( <
        div className = "App" >
        <
        body className = "wrapper" >
        <
        div className = "page" >
        <
        Header / >
        <
        Main onEditProfile = { handleEditProfileClick }
        onAddPlace = { handleAddPlaceClick }
        onEditAvatar = { handleEditAvatarClick }
        userName = { handleUserName }
        /> <
        Footer / >

        <
        PopupWithForm isOpen = { isEditProfilePopupOpen }
        onClose = { closeAllPopups }
        title = { 'Редактировать профиль' }
        name = { 'edit' }
        buttonText = { 'Сохранить' }
        children = {
            ( <
                >
                <
                input type = "text"
                placeholder = "Имя"
                className = "popup__input popup__input_type_name"
                name = "name"
                id = "nameInput"
                minlength = "2"
                maxlength = "40"
                required / >
                <
                span className = "popup__error"
                id = "nameInput-error" > < /span> <
                input type = "text"
                placeholder = "Описание"
                className = "popup__input  popup__input_type_job"
                name = "about"
                id = "jobInput"
                minlength = "2"
                maxlength = "200"
                required / >
                <
                span className = "popup__error"
                id = "jobInput-error" > < /span> <
                />
            )
        }
        /> <
        PopupWithForm isOpen = { isEditAvatarPopupOpen }
        onClose = { closeAllPopups }
        title = { 'Обновить аватар' }
        name = { 'avatar' }
        buttonText = { 'Сохранить' }
        children = {
            ( <
                >
                <
                input type = "url"
                placeholder = "Обновить аватар"
                className = "popup__input popup__input_type_avatar"
                name = "avatar"
                id = "editAvatar"
                required / >
                <
                span className = "popup__error"
                id = "editAvatar-error" > < /span> <
                />
            )
        }
        /> <
        PopupWithForm isOpen = { isAddPlacePopupOpen }
        onClose = { closeAllPopups }
        title = { 'Новое место' }
        name = { 'add-card' }
        buttonText = { 'Создать' }
        children = {
            ( <
                >
                <
                input type = "text"
                placeholder = "Название"
                className = "popup__input popup__input_type_card-name"
                name = "name"
                id = "cardname-input"
                minlength = "2"
                maxlength = "30"
                required / >
                <
                span className = "popup__error"
                id = "cardname-input-error" > < /span> <
                input type = "url"
                placeholder = "Ссылка на картинку"
                className = "popup__input  popup__input_type_card-link"
                name = "link"
                id = "link-input"
                required / >
                <
                span className = "popup__error"
                id = "link-input-error" > < /span> <
                />
            )
        }
        />

        {
            /* <div className="popup popup_type_edit">

                        <div className="popup__container">
                            <button type="button" className="popup__close"></button>
                            <h2 className="popup__text"></h2>
                            <form action="submit" className="popup__form" name="edit" novalidate>
                                <input type="text" value="Имя" placeholder="Имя" className="popup__input popup__input_type_name" name="name" id="nameInput" minlength="2" maxlength="40" required/>
                                <span className="popup__error" id="nameInput-error"></span>
                                <input type="text" value="Описание" placeholder="Описание" className="popup__input  popup__input_type_job" name="about" id="jobInput" minlength="2" maxlength="200" required/>
                                <span className="popup__error" id="jobInput-error"></span>
                                <button type="submit" className="popup__save">Сохранить</button>
                            </form>
                        </div>
                    </div> */
        }

        {
            /* <div className="popup popup_type_add-card">

                        <div className="popup__container">
                            <button type="button" className="popup__close"></button>
                            <h2 className="popup__text">Новое место</h2>
                            <form action="submit" className="popup__form" name="card" novalidate>
                                <input type="text" placeholder="Название" className="popup__input popup__input_type_card-name" name="name" id="cardname-input" minlength="2" maxlength="30" required/>
                                <span className="popup__error" id="cardname-input-error"></span>
                                <input type="url" placeholder="Ссылка на картинку" className="popup__input  popup__input_type_card-link" name="link" id="link-input" required/>
                                <span className="popup__error" id="link-input-error"></span>
                                <button type="submit" disabled="true" className="popup__save">Создать</button>
                            </form>
                        </div>
                    </div> */
        }

        <
        div className = "popup popup_type_delete-confirm" >

        <
        div className = "popup__container popup__container_type_delete" >
        <
        button type = "button"
        className = "popup__close" > < /button> <
        form action = "submit"
        className = "popup__form popup__form_delete"
        name = "delete"
        novalidate >
        <
        h2 className = "popup__text popup__text_delete" > Вы уверены ? < /h2> <
        button type = "submit"
        className = "popup__save popup__save_confirmation" > Да < /button> <
        /form> <
        /div> <
        /div>

        {
            /* <div className="popup popup_type_avatar">

                        <div className="popup__container ">
                            <button type="button" className="popup__close"></button>
                            <h2 className="popup__text ">Обновить аватар</h2>
                            <form action="submit" className="popup__form " name="avatarForm" novalidate>
                                <input type="url" placeholder="Обновить аватар" className="popup__input popup__input_type_avatar" name="avatar" id="editAvatar" required/>
                                <span className="popup__error" id="editAvatar-error"></span>
                                <button type="submit" className="popup__save ">Сохранить</button>
                            </form>
                        </div>
                    </div> */
        }

        <
        template className = "card-template" >
        <
        div className = "card__elements" >
        <
        button className = "card__delete" > < /button> <
        img src = "#"
        className = "card__photo"
        alt = "" / >
        <
        div className = "card__text" >
        <
        h2 className = "card__tittle" > < /h2> <
        div className = "card__column" >
        <
        button type = "button"
        className = "card__like" > < /button> <
        span className = "card__like-count" > < /span> <
        /div> <
        /div> <
        /div> <
        /template> <
        /div> <
        /body> <
        /div>
    );
}

export default App;
